        private async Task EnsureCreatedAsync(string key, string value)
        {
            var existingSystemValue = Get(sv => sv.Key == key);
            if (existingSystemValue != null)
                return;

            await CreateAsync(new SystemValue
            {
                Key = key,
                Value = value,
                CreatedUTC = DateTime.UtcNow,
            });
        }
    }
